$(document).ready(function() {
    var $headerElement = $('header.denver-header');
    var $topNavElements = $('ul.nav.denver-navbar-nav');
    var headerHeight = $headerElement.height();
    var hasSubNav = !$headerElement.hasClass("denver-no-subnav");

    var $accordions = $('.denver-accordion-panel-body');

    $accordions.on('show.bs.collapse', function(e) {
        $(e.currentTarget).parent().find('.denver-accordion-plus').html('<i class="glyphicon glyphicon-minus"></i>');
        $(e.currentTarget).parent().find('.panel-heading').addClass('active');
    });

    $accordions.on('shown.bs.collapse', function(e) {
        var scrollDestination = parseInt($(e.currentTarget).parent().offset().top);

        if ((hasSubNav && $topNavElements.is(':visible')) || (!hasSubNav && $topNavElements.is(':visible'))) {
            scrollDestination = scrollDestination - headerHeight;
        }

        $('html, body').animate({
            scrollTop: parseInt(scrollDestination)
        }, 250);

        setTimeout(function() { $(window).trigger('resize'); }, 300);
    });

    $accordions.on('hide.bs.collapse', function(e) {
        $(e.currentTarget).parent().find('.denver-accordion-plus').html('<i class="glyphicon glyphicon-plus"></i>');
        $(e.currentTarget).parent().find('.panel-heading').removeClass('active');
    });

    $accordions.on('hidden.bs.collapse', function(e) {
        setTimeout(function() { $(window).trigger('resize'); }, 300);
    });

    $(window).resize(function() {
        headerHeight = $headerElement.height();
    });
});

